package conditionals;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sayi=23;
		if (sayi<20) {
			System.out.println("Sayı 20den küçüktür");
		}else if (sayi==20) {
			System.out.println("Sayı 20'ye eşittir");
		}else {
			System.out.println("Sayı 20den büyüktür");
		}
	}

}
